/**
 *  Books component
 *
 *
 */

import gql from 'graphql-tag';
import Book from './Book';

import { useQuery } from '@apollo/react-hooks';

const QUERY_BOOKS = gql`
  query QUERY_BOOKS {
    books {
      book_id
      title
      description
      type
      created
      author {
        author_id
        name
        email
      }
    }
  }
`;

function Books() {
  let { loading, data, error } = useQuery(QUERY_BOOKS);

  if (loading) return <p>loading...</p>;
  if (error) throw Error(error);

  if (data) {
    console.log(data);
  }

  return (
    <article className='books'>
      {data.books.map(book => (
        <Book id={book.book_id} book={book} />
      ))}
    </article>
  );
}

export default Books;
