import styled from 'styled-components';
import Author from './Author';

const BookStyles = styled.section``;

function Book({ data }) {
  return (
    <section className={`book book${3}`}>
      <header>
        <h2 className='description'>{data.title}</h2>
      </header>
      <div className='book-meta'>
        <div className='description'>
          <p>{data.description}</p>
        </div>
      </div>
      <div className='type'>
        <span>{data.type}</span>
      </div>
    </section>
  );
}

export default Book;
