import styled from 'styled-components';
import Author from './Author';

const BookStyles = styled.section`
  box-shadow: 0 0 2px 2px rgba(0.3, 1, 0, 1);
  padding: 1.5em;
  margin-bottom: 1em;
  h2,
  p {
    padding: 0;
    margin: 0;
  }
`;

function Book({ data }) {
  return (
    <BookStyles className={`book book${3}`}>
      <header>
        <h2 className='description'>{data.title}</h2>
      </header>
      <div className='book-meta'>
        <div className='description'>
          <p>{data.description}</p>
        </div>
      </div>
      <div className='type'>
        <span>{data.type}</span>
      </div>
    </BookStyles>
  );
}

export default Book;
