import styled from 'styled-components';
import Author from './Author';

const BookStyles = styled.section`
  border: solid 1px red;
  padding: 1.5em;
  margin-bottom: 1em;
`;

function Book({ data }) {
  return (
    <BookStyles className={`book book${3}`}>
      <header>
        <h2 className='description'>{data.title}</h2>
      </header>
      <div className='book-meta'>
        <div className='description'>
          <p>{data.description}</p>
        </div>
      </div>
      <div className='type'>
        <span>{data.type}</span>
      </div>
    </BookStyles>
  );
}

export default Book;
