import styled from 'styled-components';

const Form = styled.form`
  --global-font: 'Quattrocento', serif;
  padding: 1em;
  width: 430px;
  border: solid 1px black;
  label {
    font-family: var(--global-font);
    display: block;
    margin-bottom: 0.2em;
  }

  input {
    padding: 0.3em 0.2em;
    font-size: 1em;
    outline: none;
    box-sizing: border-box;
    width: 100%;
  }

  .form {
    margin-bottom: 1.2em;
  }
`;

export default Form;
